/* eslint-disable no-useless-constructor */
/* eslint-disable jsx-a11y/alt-text */
import React ,{Component} from "react";

let arr1=["ayman","talal","hmdi",],arr2=[];
console.log(3)
for(let i=0;i<4;i++){
  console.log("i="+i)
  console.log(`arr1[index]=${arr1[0]}`)
  arr2.p(i)
  console.log(`arr1= [${arr1}] and arr2 =[${arr2}] `)
  console.log("#".repeat(15))


}

class ConCard extends Component  {

  render (){
    return (
        <div className="col-5 bg-dark text-white m-1 p-3 rounded border">
          <img src={this.props.img} />
          <h3>{this.props.name}</h3>
          <h6>phone {this.props.phone}</h6>
          <h6 style={{ color: this.props.email ? "white" : "red" }}>
            {this.props.email ? `emile${this.props.email}` : "no found email"}
          </h6>
        </div>
      );
  }
  }

export default ConCard
