import { Button } from 'react-bootstrap'
import React , { useRef ,useContext}  from 'react'
import { providerContext } from './MyContecest';

const UseRef = () => {
    const {val , changeVal} = useContext(providerContext);
  
    const va = useRef(null);
    const focus = ()=>{
        va.current.focus()
        changeVal(va.current.value)
        console.log(val)
    }
  return (
    <div>
        <input  ref={va}/>
      <Button onClick={focus}>focus</Button>
      <h1>{val}</h1>
      
    </div>
  )
}

export default UseRef
