/** @format */

import React from "react";
import { DataStudent } from "../DataStudent";

const Teble = ({ deletAll, delateStudent }) => {
    // delete data one student
  const deleteOneStudent = (ID) => {
    const index = DataStudent.findIndex((item) => item.id === ID);
    DataStudent.splice(index, 1);
    delateStudent(DataStudent);
  };
  return (
    <div className="container">
      <h2 className="text-center py-4">جدول الطلاب</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">الرقم الفريد</th>
            <th scope="col">اسم الطالب</th>
            <th scope="col">الصف</th>
            <th scope="col">الهاتف</th>
            <th scope="col">العنوان</th>
            <th scope="col">العملية</th>
          </tr>
        </thead>
        <tbody>
          {DataStudent.length >= 1 ? (
            DataStudent.map((itme, i) => {
              return (
                <tr key={i}>
                  <th scope="row">{itme.id}</th>
                  <td>{itme.n}</td>
                  <td>{itme.s}</td>
                  <td>{itme.p}</td>
                  <td>{itme.a}</td>
                  <td>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => {
                        deleteOneStudent(itme.id);
                      }}>
                      حذف
                    </button>
                  </td>
                </tr>
              );
            })
          ) : (
            <tr>
              <td colspan="6">
                <div
                  className="alert alert-danger w-100 text-center fs-2"
                  role="alert">
                  لا يوجد بيانات للعرض
                </div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
      {DataStudent.length >= 1 ? (
        <button
          type="button"
          className="btn btn-danger w-100 my-4 p-2 d-flex justify-content-center"
          onClick={deletAll}>
          مسح الكل
        </button>
      ) : null}
    </div>
  );
};

export default Teble;
