/** @format */

import React, { useState } from "react";
import MyForm from "./component/MyForm";
import Teble from "./component/Teble";
import { DataStudent } from "./DataStudent";

const App = () => {
  const [dataStudent , setDataStudent] = useState(DataStudent);
  
  // add data one student
  const AddStudent = ()=> {
    localStorage.
    setDataStudent([...dataStudent])
  }
  // delete data all students
  const deletAll = () =>{
    DataStudent.splice(0)
    setDataStudent([]);
  }
  // delete data one student
  const delateStudent = (student) =>{
    setDataStudent([...student])
  }
  return (
    <div className="color-body font">
      <MyForm AddStudent={AddStudent}/>
      <Teble deletAll = {deletAll}  delateStudent = {delateStudent}/>
    </div>
  );
};

export default App;
