import { Button } from 'react-bootstrap';
import React , {useState} from 'react'

const App = () => {
  const [val , setVal] = useState(6);
  const handelIcre = ()=>{
    setVal(val + 1)
  }
  const handelsalp = ()=>{
    setVal(val - 1)
  }
  return (
    <div>
      <Button onClick={handelIcre}>+</Button> {'  '}
      <Button onClick={handelsalp}>-</Button> {'  '}
      <h1>value naw is = {val}</h1>
      <br></br>
      <input v></input>
    </div>
  )
}

export default App
