/* eslint-disable no-useless-constructor */
import React,{Component} from "react";
class App extends Component{
    constructor(){
      super()
      this.state={
        num:0
      }

      this.evientHendler=this.evientHendler.bind(this);
    }

    evientHendler(){
     this.setState({
      num:this.state.num+1
     })
    }

  render( ){
    return(
      <div>
        <h1>{this.state.num}</h1>
      <button onClick={this.evientHendler} className="btn btn-primary">click me</button>
      </div>
    )
  }
}

export default App
