import React, { useState } from 'react'
import { DataStudent } from '../DataStudent'

const MyForm = ({onAdd}) => {
    const [Sn , setSn] = useState('')
    const [Ss , setSs] = useState('')
    const [Sp , setSp] = useState('')
    const [Sa , setSa] = useState('')
    const addStudent = () => {
        console.log(`${Sn} ${Ss} ${Sp} ${Sa} ${Math.random(1)}`)
        DataStudent.push({n:Sn, s : Ss , p : Sp , a : Sa})
        console.log(DataStudent)
        onAdd()  
    }
  return (
    <div className="container border-bottom px-0">
        <h2 className="text-center py-4">ادخل طالب جديد</h2>
        <div className="row">
          <div className="col-sm-3">
          <input className="form-control" type="text" placeholder="ادخل اسم الطالب" onChange={(e)=>{setSn(e.target.value)}}/>
          </div>
          <div className="col-sm-3">
          <input className="form-control" type="text" placeholder="ادخل صف الطالب" onChange={(e)=>{setSs(e.target.value)}}/>
          </div>
          <div className="col-sm-3">
          <input className="form-control" type="text" placeholder="ادخل هاتف الطالب " onChange={(e)=>{
            setSp(e.target.value)
          }} />
          </div>
          <div className="col-sm-3">
          <input className="form-control" type="text" placeholder="ادخل عنوان الطالب" onChange={(e)=>{
            setSa(e.target.value)
          }}/>
          </div>
        </div>
        <button type="button" className="btn btn-primary w-100 my-4 p-2 d-flex justify-content-center" onClick={addStudent}>إضــــافة</button>
      </div>
  )
}

export default MyForm
